    public boolean increasingTriplet(int[] nums) {
        if (nums == null || nums.length < 3) {
            return false;
        }
        int i1 = nums[0];
        int i2 = nums[0];
        int i3 = nums[0];
        for (int i = 0; i < nums.length; i++) {
            if (i2 == nums[0] && nums[i] != i1) {
                i2 = nums[i];
            }
            if (i3 == nums[0] && nums[i] != i2 && nums[i] != i1) {
                i3 = nums[i];
            }
        }
        if (i1 == i2 || i1 == i3 || i2 == i3) {
            return false;
        }
        int lastVal;
        for (int i = 0; i < nums.length - 2; i++) {
            lastVal = nums[i];
            for (int j = i + 1; j < nums.length - 1; j++) {
                if (nums[j] <= lastVal) {
                    continue;
                }
                lastVal = nums[j];
                for (int k = j + 1; k < nums.length; k++) {
                    if (nums[k] <= lastVal) {
                        lastVal = nums[i];
                        continue;
                    }
                    if (nums[i] < nums[j] && nums[j] < nums[k]) {
                        return true;
                    }
                }
            }
        }
        return false;
    }
